import React, { useState, useEffect } from 'react'

import { useNavBar, useMouseMove } from './../helpers/hooks';

// components
import FunctionUI from '../components/FunctionUI';
import Nav from './../components/Nav';
import About from './../components/About';
import List from './../components/List';
import Gallery from './../components/Gallery';
import Image from './../components/Image';

const Portfolio = ({ data }) => {

    // set the lenguage from the props
    const [leng, setLeng] = useState(data);
    // display the functionUI
    const [display, setDisplay] = useState("portfolio");
    // change the menu icon
    const [active, setActive] = useState(false);
    // set the image
    const [img, setImg] = useState("");

    const [[linearGradient, scroll], documentHeight, height] = useNavBar("#721e25", "#969696");
    // scroll position
    const [scrollPosition, setScrollPosition] = useState(0)


    useEffect(() => console.log(scrollPosition))

    if (display === "functionUI")
        return (
            <FunctionUI
                leng={leng}
                setDisplay={setDisplay}
                display={display}
                setActive={setActive}
            />
        )
    if (display === "portfolio") return <>
        <Nav
            leng={leng}
            active={active}
            setActive={setActive}
            setDisplay={setDisplay}
            setLeng={setLeng}
            scrollPosition={scrollPosition}
            setScrollPosition={setScrollPosition}
        />

        {active ? <List
            setLeng={setLeng}
            leng={leng}
            setActive={setActive}
        /> : <>
                <About leng={leng} />
                <Gallery
                    setDisplay={setDisplay}
                    setImg={setImg}
                    leng={leng} />
                <p className='name'>Â© Arturo Rugh</p>
            </>
        }
    </>
    if (display === "img")
        return <Image
            img={img}
            setDisplay={setDisplay}
        />
}


export default Portfolio;
