import React, { useState, useEffect } from 'react';
import axios, { post, get, put } from 'axios';

const Welcome = props => {

    // check if a lenguage has already been stored
    const lenguage = localStorage.getItem('art-web-leng');
    // store the lenguage in hook
    const [leng, setLeng] = useState(lenguage);

    const [response, getResponse] = useState(undefined)

    // hook where the radios are stored
    const [radios, setRadios] = useState();
    const [isErr, setisErr] = useState()

    const fetchData = async () => {

        // fetch data
        const res = await getData();

        // unsucced fetch
        if (!res) {
            // catch errors and display UI
            setisErr(true)

        } else if (res.status === 200) {
            // store the data in the hook
            setRadios(res.data.radios);
            console.log("radios stored");
        }
    }

    useEffect(() => {
        document.title = "arturo rugh";
        fetchData()
        console.log(radios);

    }, [radios])



    const getData = async () => {

        try {
            const res = await get('http://localhost:4000/projects');
            return res

        } catch (err) {
            console.log(err);
            return err.response
        }
    };

    const getProjects = async () => {
        const result = await get(
            'http://localhost:4000/projects',
        )
        return result
    };


    return (leng === null) ?
        <div className="welcome-container">
            <h1> Arturo Rugh</h1>
            <h2>-  welcome -</h2>
            <h2>select the language</h2>
            <div className="leng-bottons">
                <button
                    onClick={() => {
                        localStorage.setItem('art-web-leng', 'english')
                        setLeng('english')
                    }}
                >english</button>
                <button
                    onClick={() => {
                        localStorage.setItem('art-web-leng', 'espanol')
                        setLeng('espanol')
                    }}
                >espa√±ol</button>
            </div>
        </div>
        : props.render(leng)
}

export default Welcome;
