import React, { useState } from 'react';
import { v4 as uuidv4 } from "uuid";

const ImgDisplayer = ({ data, indx }) => {

    const [counter, setCounter] = useState(0);
    // console.log(Object.keys(data).toString());
    // console.log(data.projects[0].paz.title);

    return (
        <>
        <h1>{Object.keys(data).title} </h1>
            <div
                className="img-container"
                id={Object.keys(data).toString()}                >
                <div className="gallery">
                    {Object.values(data)[0].images.map((img, ind) =>
                        counter === ind &&
                        img.img.map(img => <img
                            alt={Object.keys(data).toString()}
                            className="main-image"
                            key={uuidv4()}
                            src={require(`./../assets/photos/${img}.png`)} />)
                    )}
                    <div
                        key={uuidv4()}
                        className="img-selector">
                        {Object.values(data)[0].images.map((img, index) =>
                            index !== counter &&
                            img.img.map(img =>
                                <img
                                    alt={Object.keys(data).toString()}
                                    className="mini-img"
                                    key={uuidv4()}
                                    src={require(`./../assets/photos/${img}.png`)}
                                    onClick={() => setCounter(index)}
                                />
                            )
                        )}
                    </div>
                </div>
                <div className="proj-description">
                    {Object.values(data)[0].description["espanol"]}
                </div>
            </div>
        </>
    )
}

export default ImgDisplayer;
