import React, { useState } from 'react';
import { v4 as uuidv4 } from "uuid";

const ImgDisplayer = ({ data, leng }) => {

    const [counter, setCounter] = useState(0);

    data = Object.values(data)[0];

    return (
        <div className="image"
            id={Object.keys(data)}  >
            <h1>{data.title} </h1>
            <h2>{data.type[leng]}</h2>
            <h2>{data.date}</h2>
            {data.links && <h2>{data.links}</h2>}
            <div
                className="img-container">
                <div className="gallery">
                    {data.images.map((img, ind) =>
                        counter === ind &&
                        img.img.map(img => <img
                            alt={data}
                            className="main-image"
                            key={uuidv4()}
                            src={require(`./../assets/photos/${img}.png`)} />)
                    )}
                    <div
                        key={uuidv4()}
                        className="img-selector">
                        {data.images.map((img, index) =>
                            index !== counter &&
                            img.img.map(img =>
                                <img
                                    alt={data}
                                    className="mini-img"
                                    key={uuidv4()}
                                    src={require(`./../assets/photos/${img}.png`)}
                                    onClick={() => setCounter(index)}
                                />
                            )
                        )}
                    </div>
                </div>
                <div className="proj-description">
                    {data.description[leng]}
                </div>
            </div>
        </div>
    )
}

export default ImgDisplayer;
