import React from 'react';

// hooks
import { useHandleCursor } from './../helpers/hooks';

import { projects } from './../data';

export default function Image({ setPortfolio, img, scrollPosition }) {

    const [ index, images, handleCursor] = useHandleCursor(projects, img, setPortfolio, scrollPosition);

    return (
        <figure id="img_diplayer">
            < img
                style={{
                    cursor: handleCursor.cursor
                }}
                onClick={() => handleCursor.event()}
                alt={img.alt}
                className="main-image"
                src={require(`./../assets/photos/${images[index]}.png`)}
            />
            <figcaption>{img.alt}</figcaption>
        </figure>
    )
}
