import React, { useState, useEffect } from 'react'

const Welcome = props => {
    const lenguage = localStorage.getItem('art-web-leng');


    const [leng, setLeng] = useState(null);
    console.log(leng);

    useEffect(() => {
        document.title = "Arturo Rugh";
        // setLeng(lenguage)

    }, [lenguage])

    return (leng === null) ?
        <div className="welcome-container">
            <h1> Welcome!</h1>
            <h2>select the lenguage</h2>
            <div className="leng-bottons">
                <button
                    onClick={() => {
                        localStorage.setItem('art-web-leng', 'english')
                        setLeng('english')
                    }}
                >english</button>
                <button
                    onClick={() => {
                        localStorage.setItem('art-web-leng', 'spanish')
                        setLeng('espanol')
                    }}
                >espa√±ol</button>
            </div>
        </div>
        : props.render(leng)
}

export default Welcome;
