    //     let documentHeight = document.body.scrollHeight

    //     // set porcentages
    //     let left = Math.floor(scroll * 100 / documentHeight);
    //     let right = 100 - left;
    //     // set the border Image strings
    //     let linearGradient = left > 50 ?
    //         `linear-gradient(to right, ${red} ${left}%, ${grey} ${right}%)1` :
    //         `linear-gradient(to left, ${grey} ${right}%, ${red} ${left}%)1`

    //     // store on the hook
    //     return setScrollPorcent(linearGradient)

    // })

    // display the nav when the web is scrolled and hide it after 5 second and 
    useEffect(() => {

        window.addEventListener('scroll', () => {
            // get the total of the window height
            let windowHeight =  document.body.getBoundingClientRect().height - window.innerHeight - 200
            let a = document.body.scrollHeight
