const Welcome = props => {
    const lenguage = localStorage.getItem('art-we');
    const [leng, setLeng] = useState("");

    useEffect(() => {
        document.title = "Arturo Rugh"
    }, [])

    return (leng === '') ?
        <div className="welcome-container">
            <h1> Welcome!</h1>
            <h2>select the lenguage</h2>
            <div className="leng-bottons">
                <button
                    onClick={() => setLeng('english')}
                >english</button>
                <button
                    onClick={() => setLeng('espanol')}
                >espa√±ol</button>
            </div>
        </div>
        : props.render(leng)
}

export default Welcome;
