import React, { useState, useEffect } from 'react';

import MenuIcon from './MenuIcon';
import FunctionIcon from './FunctionIcon';

// hooks
import {  useNavBar } from './../helpers/hooks'

const Nav = ({ active, setActive, setDisplay, leng, setLeng }) => {

    // set the style of the menu-icon -horizontals or diagonals
    const [displayNav, setDisplayNav] = useState(true);

    const [scrollPorcent, documentHeight, height] = useNavBar("#721e25", "#969696");

    // display the nav when the web is scrolled and hide it after 5 second and 
    useEffect(() => {

        window.addEventListener('scroll', () => {

            setDisplayNav(true);
            setTimeout(() => {
                if (window.pageYOffset > 107
                    && !active
                    && window.pageYOffset < (documentHeight - height - 200)) setDisplayNav(false)
            }, 10000)
        });
    }, [displayNav, active, documentHeight]);

    return (
        <div className="nav-container"
            style={{
                opacity: displayNav ? 1 : 0,
                zIndex: displayNav ? 2 : 1,
                borderImage: scrollPorcent
            }}>
            <h1
                id="logo"
                onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            >Arturo Rugh</h1>
            <div className="nav-buttons">
                <div
                    className="leng"
                    onClick={() => {
                        localStorage.setItem('art-web-leng', leng === 'espanol' ? 'english' : 'espanol');
                        setLeng(leng === 'espanol' ? 'english' : 'espanol')
                    }} >{leng === "espanol" ? "english" : "español"}</div>

                <div
                    className="function-button"
                    style={{
                        height: displayNav ? "auto" : 0
                    }}
                    onClick={() => {
                        setDisplay(true)
                        setActive(false)
                    }}>
                    <FunctionIcon
                        displayNav={displayNav}
                    />
                </div>
                <MenuIcon
                    displayNav={displayNav}
                    active={active}
                    setActive={setActive}
                />
            </div>
        </div>
    )
}

export default Nav;
