import React, { useState, useEffect } from 'react'

import { useNavBar } from './../helpers/hooks';

// components
import FunctionUI from '../components/FunctionUI';
import Nav from './../components/Nav';
import About from './../components/About';
import List from './../components/List';
import Gallery from './../components/Gallery';
import Image from './../components/Image';

const Portfolio = ({ data }) => {

    // set the lenguage from the props
    const [leng, setLeng] = useState(data);
    // display the functionUI
    const [display, setDisplay] = useState("portfolio");
    // change the menu icon
    const [portfolioController, setPortfolio] = useState("gallery");
    // set the image
    const [img, setImg] = useState("");

    // const [[linearGradient, scroll], documentHeight, height] = useNavBar("#721e25", "#969696");
    // scroll position
    const [scrollPosition, setScrollPosition] = useState(0)


    useEffect(function check() {
        console.log(portfolioController)
        if (portfolioController !== "gallery") {
            window.scrollTo({ top: 0, behavior: 'auto' })
            console.log(portfolioController, scrollPosition)
        } else { window.scrollTo({ top: scrollPosition, behavior: 'auto' }) }
    }, [portfolioController])

    return <>

        <Nav
            leng={leng}
            portfolioController={portfolioController}
            setPortfolio={setPortfolio}
            // setDisplay={setDisplay}
            // display={display}
            setLeng={setLeng}

            scrollPosition={scrollPosition}
            setScrollPosition={setScrollPosition}
        />
        {(portfolioController  === "functionUI") &&
            <FunctionUI
                leng={leng}
                // setDisplay={setDisplay}
                display={display}
                setPortfolio={setPortfolio}
            />}
        {portfolioController === "list" && <List
            setLeng={setLeng}
            leng={leng}
            setPortfolio={setPortfolio}
        />}
        {portfolioController === "gallery" && <>
            <About leng={leng} />
            <Gallery
                setPortfolio={setPortfolio}
                setDisplay={setDisplay}
                setImg={setImg}
                leng={leng} />
            <p className='name'>Â© Arturo Rugh</p>
        </>}
        {portfolioController === "img" &&
            <Image
                scrollPosition={scrollPosition}
                img={img}
                setPortfolio={setPortfolio}
            />
        }

    </>
}


export default Portfolio;
