import React, { useState } from 'react';
import { v4 as uuidv4 } from "uuid";

const ImgDisplayer = ({ data, leng }) => {

    const [counter, setCounter] = useState(0);
    // console.log(Object.keys(data).toString());
    // console.log(Object.values(data)[0].links);

    return (
        <div className = "image">
            <h1>{Object.values(data)[0].title} </h1>
            <h2>{Object.values(data)[0].type[leng]}</h2>
            <h2>{Object.values(data)[0].date}</h2>
            {Object.values(data)[0].links && <h2>{Object.values(data)[0].links}</h2>}
            <div
                className="img-container"
                id={Object.keys(data).toString()}                >
                <div className="gallery">
                    {Object.values(data)[0].images.map((img, ind) =>
                        counter === ind &&
                        img.img.map(img => <img
                            alt={Object.keys(data).toString()}
                            className="main-image"
                            key={uuidv4()}
                            src={require(`./../assets/photos/${img}.png`)} />)
                    )}
                    <div
                        key={uuidv4()}
                        className="img-selector">
                        {Object.values(data)[0].images.map((img, index) =>
                            index !== counter &&
                            img.img.map(img =>
                                <img
                                    alt={Object.keys(data).toString()}
                                    className="mini-img"
                                    key={uuidv4()}
                                    src={require(`./../assets/photos/${img}.png`)}
                                    onClick={() => setCounter(index)}
                                />
                            )
                        )}
                    </div>
                </div>
                <div className="proj-description">
                    {Object.values(data)[0].description[leng]}
                </div>
            </div>
        </div>
    )
}

export default ImgDisplayer;
