import React, { useState, useEffect } from 'react'

import { useNavBar } from './../helpers/hooks';

// components
import FunctionUI from '../components/FunctionUI';
import Nav from './../components/Nav';
import About from './../components/About';
import List from './../components/List';
import Gallery from './../components/Gallery';
import Image from './../components/Image';

const Portfolio = ({ data }) => {

    // set the lenguage from the props
    const [leng, setLeng] = useState(data);
    // display the functionUI
    const [display, setDisplay] = useState("portfolio");
    // change the menu icon
    const [portfolioController, setPortfolio] = useState("gallery");
    // set the image
    const [img, setImg] = useState("");

    // const [[linearGradient, scroll], documentHeight, height] = useNavBar("#721e25", "#969696");
    // scroll position
    const [scrollPosition, setScrollPosition] = useState(0)


    useEffect(() => 
    (portfolioController == "gallery") && console.log(portfolioController, scrollPosition)
    // window.scrollTo({ top: scrollPosition, behavior: 'auto' })
    , [scrollPosition])

    if (display === "functionUI")
        return (
            <FunctionUI
                leng={leng}
                setDisplay={setDisplay}
                display={display}
            // setPortfolio={setPortfolio}
            />
        )
    if (display === "portfolio") return <>
        <Nav
            leng={leng}
            portfolioController={portfolioController}
            setPortfolio={setPortfolio}
            setDisplay={setDisplay}
            display={display}
            setLeng={setLeng}
            scrollPosition={scrollPosition}
            setScrollPosition={setScrollPosition}
        />

        {portfolioController === "list" && <List
            setLeng={setLeng}
            leng={leng}
            setPortfolio={setPortfolio}
        />}
        {portfolioController === "gallery" && <>
            <About leng={leng} />
            <Gallery
                setPortfolio={setPortfolio}
                setDisplay={setDisplay}
                setImg={setImg}
                leng={leng} />
            <p className='name'>Â© Arturo Rugh</p>
        </>}
        {portfolioController === "img" &&
            <Image
                img={img}
                setPortfolio={setPortfolio}
            />
        }

    </>
    // if (display === "img")
    //     return <Image
    //         img={img}
    //         setDisplay={setDisplay}
    //     />
}


export default Portfolio;
