import React, { useState, useEffect } from 'react';

import MenuIcon from './MenuIcon';
import FunctionIcon from './FunctionIcon';

// hooks
import { useScrollPosition, useWindowDimensions } from './../helpers/hooks'

const Nav = ({ active, setActive, setDisplay, leng, setLeng }) => {

    // set the style of the menu-icon -horizontals or diagonals
    const [displayNav, setDisplayNav] = useState(true);

    const { width, height } = useWindowDimensions();



    const [scrollPorcent, setScrollPorcent] = useState(``)

    useScrollPosition(({ prevPos, currPos }) => {

        const grey = "#969696"
        const red = "#721e25"

        // get the scrollposition if < 50 windowlenght  ? position : bottom position
        let scroll = Math.abs(currPos.y) < height ? Math.abs(currPos.y) : Math.abs(currPos.y) + height;
        // get the total height of the document
        let documentHeight = document.body.scrollHeight

        // set porcentages
        let left = Math.floor(scroll * 100 / documentHeight);
        let right = 100 - left;
        // set the border Image strings
        let linearGradient = left > 50 ?
            `linear-gradient(to right, ${red} ${left}%, ${grey} ${right}%)1` :
            `linear-gradient(to left, ${grey} ${right}%, ${red} ${left}%)1`

        // store on the hook
        return setScrollPorcent(linearGradient)

    })

    // display the nav when the web is scrolled and hide it after 5 second and 
    useEffect(() => {

        window.addEventListener('scroll', () => {
            // get the total of the window height
            let windowHeight = document.body.getBoundingClientRect().height - window.innerHeight - 200
            console.log(win);

            setDisplayNav(true);
            setTimeout(() => {
                if (window.pageYOffset > 107
                    && !active
                    && window.pageYOffset < windowHeight) setDisplayNav(false)
            }, 10000)

        });
    }, [displayNav, active]);

    return (
        <div className="nav-container"
            style={{
                opacity: displayNav ? 1 : 0,
                zIndex: displayNav ? 2 : 1,
                borderImage: scrollPorcent
            }}>
            <h1
                id="logo"
                onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            >Arturo Rugh</h1>
            <div className="nav-buttons">
                <div
                    className="leng"
                    onClick={() => {
                        localStorage.setItem('art-web-leng', leng === 'espanol' ? 'english' : 'espanol');
                        setLeng(leng === 'espanol' ? 'english' : 'espanol')
                    }} >{leng === "espanol" ? "english" : "español"}</div>

                <div
                    className="function-button"
                    style={{
                        height: displayNav ? "auto" : 0
                    }}
                    onClick={() => {
                        setDisplay(true)
                        setActive(false)
                    }}>
                    <FunctionIcon
                        displayNav={displayNav}
                    />
                </div>
                <MenuIcon
                    displayNav={displayNav}
                    active={active}
                    setActive={setActive}
                />
            </div>
        </div>
    )
}

export default Nav;
