import React, { useEffect } from 'react';

// hooks
import { useMouseMove } from './../helpers/hooks'

export default function Image({ setPortfolio, img, scrollPosition }) {

    const position = useMouseMove();

    // useEffect(() => console.log(window.innerWidth), [position])

    // console.log(window.innerWidth);

    const handleCursor = () => {
        const windowHeight = window.innerWidth;
        console.log(windowHeight / 3);

        console.log(position);

        console.log(position < windowHeight / 3);


        if (position.x < windowHeight / 3) return "url('https://img.icons8.com/ios/30/ffffff/double-left.png'), auto";
        else if (position.x > windowHeight / 3 * 2) return "url('https://img.icons8.com/ios/30/ffffff/double-right.png'), auto"
        else return "url('https://img.icons8.com/ios/30/ffffff/delete-sign.png'), auto";
    }


    return (
        <div id="bigimg_container">
            < img
                style={{
                    cursor: handleCursor()
                    // position.x > 100 ? "url('https://img.icons8.com/ios/30/ffffff/delete-sign.png'), auto" :
                    //     "url('https://img.icons8.com/ios/50/ffffff/double-left.png'), auto"
                }}
                onClick={() => {
                    setPortfolio("gallery")
                    window.scrollTo({ top: scrollPosition, behavior: 'auto' })
                }}
                alt={img.alt}
                className="main-image"
                src={require(`./../assets/photos/${img.img}.png`)}
            />

        </div>
    )
}
