import React, { useState, useEffect } from 'react';

// hooks
import { useMouseMove } from './../helpers/hooks';

import { projects } from './../data';

export default function Image({ setPortfolio, img, scrollPosition }) {


    const imagesList = () => {
        let images = []

        projects.map(proj =>
            (proj[Object.keys(proj)].images) &&
            proj[Object.keys(proj)].images.map(img => images.push(img.img))
        )

        return images
    }

    const getIndex = imagesList().reduce((acc, image, ind) => {
        if (image === img.img) { return acc + ind }
        return acc
    }, 0);

    const [index, setIndex] = useState(getIndex)

    const position = useMouseMove();


    const handleCursor = () => {
        const windowHeight = window.innerWidth;

        if (position.x < windowHeight / 3 - 100) return {
            cursor: "url('https://img.icons8.com/ios/30/ffffff/back--v1.png'), auto"
        };
        else if (position.x > (windowHeight / 3 * 2) + 100) return {
            cursor: "url('https://img.icons8.com/ios/30/ffffff/forward--v1.png'), auto"
        };
        else return {
            cursor: "url('https://img.icons8.com/ios/30/ffffff/delete-sign.png'), auto"

        }
    }


    return (
        <div id="bigimg_container">
            < img
                style={{
                    cursor: handleCursor().cursor
                }}
                onClick={() => {
                    setPortfolio("gallery")
                    window.scrollTo({ top: scrollPosition, behavior: 'auto' })
                }}
                alt={img.alt}
                className="main-image"
                src={require(`./../assets/photos/${imagesList()[index]}.png`)}
            />

        </div>
    )
}
