import React, { useState } from 'react';
import { v4 as uuidv4 } from "uuid";

const ImgDisplayer = ({ data, leng }) => {

    const [counter, setCounter] = useState(0);

    const input = Object.values(data)[0];

    return (
        <div className="image"
            id={Object.keys(data)}  >
            <h1>{input.title[leng]} </h1>
            <h2>{input.type[leng]}</h2>
            <h2>{input.date}</h2>
            {input.links && <h2>{input.links}</h2>}
            <div
                className="img-container">
                <div className="img-gallery">
                    {input.images.map((img, ind) =>
                        counter === ind &&
                        img.img.map(img => <img
                            alt={Object.keys(data)}
                            className="main-image"
                            key={uuidv4()}
                            src={require(`./../assets/photos/${img}.png`)} />)
                    )}
                    <div
                        key={uuidv4()}
                        className="img-selector">
                        {input.images.map((img, index) =>
                            index !== counter &&
                            img.img.map(img =>
                                <img
                                    alt={Object.keys(data)}
                                    className="mini-img"
                                    key={uuidv4()}
                                    src={require(`./../assets/photos/${img}.png`)}
                                    onClick={() => setCounter(index)}
                                />
                            )
                        )}
                    </div>
                </div>
                <div className="proj-description">
                    {input.description[leng].map(p =>
                        <p
                        className = {p.charAt(0) === `"` || p.charAt(0) === `'` && "cite"}>{p}</p>
                    )}
                </div>
            </div>
        </div>
    )
}

export default ImgDisplayer;
