import React, { useState, useEffect } from 'react';

import MenuIcon from './MenuIcon';
import FunctionIcon from './FunctionIcon';

const Nav = ({ active, setActive, setDisplay, leng }) => {

    const [displayNav, setDisplayNav] = useState(true);

    useEffect(() => {
        window.addEventListener('scroll', () => {
            setDisplayNav(true);
            setTimeout(() => {
                if (window.pageYOffset > 107 && active === false) setDisplayNav(false)
            }, 5000)
        });
    }, [displayNav]);

    return (
        <>
            <div className="nav-container"
                style={{
                    opacity: displayNav ? 1 : 0,
                }}>
                <h1>Arturo Rugh</h1>
                <div className="nav-buttons">
                    <div
                        className="function-button"
                        style={{
                            height: displayNav ? "auto" : 0
                        }}
                        onClick={() => {
                            setDisplay(true)
                            setActive(false)
                        }}>
                        <FunctionIcon
                            displayNav={displayNav}
                        />
                    </div>
                    <MenuIcon
                        displayNav={displayNav}
                        active={active}
                        setActive={setActive}
                    />
                </div>
            </div>
            <footer
                style={{
                    opacity: displayNav ? 1 : 0,
                }}>

                <p>{leng === "espanol" ? "change lenguage to english" : "cambiar idioma a español"}</p>
            </footer>
        </>
    )
}

export default Nav;
