import React, { useState } from 'react'

// components
import FunctionUI from '../components/FunctionUI';
import Nav from './../components/Nav';
import About from './../components/About';
import List from './../components/List';
import Gallery from './../components/Gallery';


const Portfolio = ({ }) => {

    const [display, setDisplay] = useState(false);
    const [active, setActive] = useState(false);
    const leng = "espanol";


    if (display) {
        return (
            <FunctionUI
                leng={leng}
                setDisplay={setDisplay}
                display={display}
            />
        )
    } else {
        return <>
            <Nav
                active={active}
                setActive={setActive}
                setDisplay={setDisplay}
            />
            {active ? <List
                leng={leng}
                setActive={setActive}
            /> :
                <>
                    <About leng={leng} />
                    {/* <Gallery leng={leng} /> */}
                </>
            }
            <div style={{ overflow: "hidden", position: "relative" }}>
                <iframe frameBorder="0" scrolling="no" marginHeight="0" marginWidth="0" width="100%" height="720px" type="text/html" src='https://www.youtube.com/embed/qYUEHJhv_P4?autoplay=0&fs=1&iv_load_policy=3&showinfo=0&rel=0&cc_load_policy=0&start=0&end=0&origin=https://youtube-embed.com'>
                </iframe>
                <div style={{
                    position: "absolute", bottom: "10px", left: 0, right: 0, marginLeft: "auto", marginRight: "auto", color: "#000", textAlign: "center"
                }} >
                    {/* <small style={{
                            lineHeight: "1.8", fontSize: "0px", background: "#fff"}} > */}
                    {/* <a href="https://logine.de/">logine</a> */}
                    {/* </small> */}
                </div>
                {/* <style>#gmap_canvas img{max - width:none!important;background:none!important}</style> */}
            </div>
            <iframe frameBorder="0" src= "https://www.youtube.com/embed/watch?v=qYUEHJhv_P4&t=110s"></iframe>
            <br />
        </>
    }
}

export default Portfolio;
