import React, { useState, useEffect } from 'react';

import MenuIcon from './MenuIcon';
import FunctionIcon from './FunctionIcon';


const Nav = ({ active, setActive, setDisplay, leng, setLeng }) => {

    // set the style of the menu-icon -horizontals or diagonals
    const [displayNav, setDisplayNav] = useState(true);


    // display the nav when the web is scrolled and hide it after 5 second and 
    useEffect(() => {
        console.log(window.pageYOffset, (document.body.getBoundingClientRect().height - window.innerHeight - 200));

        window.addEventListener('scroll', () => {
            let total
            setDisplayNav(true);
            if (window.pageYOffset < (document.body.getBoundingClientRect().height - window.innerHeight - 200)) {
                setTimeout(() => {
                    if (window.pageYOffset > 107
                        && !active) setDisplayNav(false)
                }, 7000)
            }
        });
    }, [displayNav, active]);

    // footer function in case the menu is displayed
    const displayFooter = () => {
        if (displayNav && !active) {
            return true
        } else {
            return false
        }
    }

    return (
        <>
            <div className="nav-container"
                style={{
                    opacity: displayNav ? 1 : 0,
                    zIndex: displayNav ? 2 : 1,
                }}>
                <h1>Arturo Rugh</h1>
                <div className="nav-buttons">
                    <div
                        className="function-button"
                        style={{
                            height: displayNav ? "auto" : 0
                        }}
                        onClick={() => {
                            setDisplay(true)
                            setActive(false)
                        }}>
                        <FunctionIcon
                            displayNav={displayNav}
                        />
                    </div>
                    <MenuIcon
                        displayNav={displayNav}
                        active={active}
                        setActive={setActive}
                    />
                </div>
            </div>
            <footer
                style={{
                    opacity: displayFooter() ? 1 : 0,
                    height: displayFooter() ? "auto" : 0,
                    float: "left"
                }}
                // change the lenguage
                onClick={() => {
                    localStorage.setItem('art-web-leng', leng === 'espanol' ? 'english' : 'espanol');
                    setLeng(leng === 'espanol' ? 'english' : 'espanol')
                }}
            >{leng === "espanol" ? "change lenguage to english" : "cambiar idioma a español"}
            </footer>

        </>
    )
}

export default Nav;
