import React, { useState, useEffect } from 'react';

import MenuIcon from './MenuIcon';
import FunctionIcon from './FunctionIcon';

const Nav = ({ active, setActive, setDisplay }) => {

    const [displayNav, setDisplayNav] = useState(true);

    useEffect(() => {
        window.addEventListener('scroll', () => {
            setDisplayNav(true);
            setTimeout(() => {
                if (window.pageYOffset > 107 && active === false) setDisplayNav(false)
            }, 5000)
        });
    }, [displayNav]);

    return (
        <div className="nav-container"
            style={{
                opacity: displayNav ? 1 : 0,
                // height: displayNav ? "flex" : "none",
            }}>
            <h1>Arturo Rugh</h1>
            <div className="nav-buttons">
                <div
                    onClick={() => {
                        setDisplay(true)
                        setActive(false)
                    }}
                >
                    <FunctionIcon />
                </div>
                <MenuIcon
                    active={active}
                    setActive={setActive}
                />
            </div>
        </div>
    )
}

export default Nav;
