                    onClick={() => {
                        // localStorage.setItem('art-web-leng', 'english')
                        setLeng('english')
                    }}
                >english</button>
                <button
                    onClick={() => {
                        // localStorage.setItem('art-web-leng', 'espanol')
                        setLeng('espanol')
                    }}
                >espa√±ol</button>
            </div>
        </>
    </div>
